qdef.select(s)
    .where(cb.not(cb.between(s.<BigDecimal>get("amount"), 
                new BigDecimal(90.00), 
                new BigDecimal(110.00))));

CriteriaQuery<Sale> qdef = cb.createQuery(Sale.class);
